INSERT INTO product (name, description, price) VALUES('MacBook Air', 'O MacBook Air de 13 polegadas com processador Apple M1 possui uma CPU de 8 núcleos e uma GPU de 7 núcleos, 8 GB de RAM e 256 GB de armazenamento SSD. Este modelo, na cor cinza espacial, é conhecido por seu desempenho eficiente, ideal para tarefas do dia a dia e trabalhos mais exigentes, além de ser leve e portátil, perfeito para uso em qualquer lugar.', 6800.00)
INSERT INTO product (name, description, price) VALUES('MacBook Pro Apple 14', 'O MacBook Pro de 14 polegadas com o processador M3 Pro da Apple possui uma CPU de 11 núcleos e uma GPU de 14 núcleos, 18 GB de RAM e 512 GB de armazenamento SSD. Este modelo, na cor preto espacial, oferece um desempenho excepcional, ideal para tarefas intensivas e profissionais. Com uma tela de alta resolução e tecnologia avançada, é perfeito para edição de vídeo, design gráfico e outras aplicações exigentes, mantendo a portabilidade e elegância características da linha MacBook Pro.', 6800.00)